package common.util;import static org.junit.Assert.*;import java.io.File;import org.junit.Test;import common.util.Zip;public class ZipTests {	@Test	public void testZip() {		String sourceDir = "input-folder";		String archive = "target/1.zip";		new Zip(sourceDir, archive);		File file = new File(archive);		assertEquals(true, file.exists());		archive = "target/2.zip";		new Zip(sourceDir, archive, "22/");		file = new File(archive);		assertEquals(true, file.exists());	}}